def makejoin(char: str) -> Callable[[tuple[str, ...], bool], str]:
    def joinstr(*strs, drop: bool = True) -> str:
        strs = tuple(map(str, tuple(vargs(strs)))) 
        strs = tuple(filter(lambda s: s, strs)) if drop else strs
        return char.join(strs)
    return joinstr

